import { useState, useEffect } from "react";
import "./App.css";
import pokemons from "./pokemons.json";
import Pokemon from "./Pokemon.jsx";

function App() {



  const [page, setPage] = useState(0);
  const [pagination, setPagination] = useState([]);

  useEffect(() => {
    let newPagination = [];
    for (let i = 0; i < pokemons.length / 50; i++) {
      newPagination.push(
        <span
          onClick={() => {
            setPage(i);
          }}
        >
          {i + 1}-
        </span>
      );
    }
    setPagination(newPagination);
  }, []);



    <div className="App">

      {pokemons.slice(page * 50, (page + 1) * 50).map((poke) => (
        <Pokemon
          key={poke.id}
          name={poke.name.english}
          type={poke.type}
          image={poke.image.thumbnail}
          base={poke.base}
        />
      ))}

      <button onClick={() => (page == 0 ? null : setPage(page - 1))}>
        Previous
      </button>
      {pagination}
      <button onClick={() => (page == 17 ? null : setPage(page + 1))}>
        Next
      </button>
    </div>
  ; 
}

export default App;
