import { useState } from 'react';
import pokemons from './pokemons.json';



function Pokemon(props){
const [layerDisplay, setLayerDisplay] = useState(false);
    return(
        <div>
            <p>{props.id}</p>
            <p>{props.name}</p>
            <p>{props.type.map((i)=><li>{i}</li>)}</p>
            <img src={props.image}/>
            <button onClick={(e)=> setLayerDisplay(!layerDisplay)}>Leer mas</button>
            <div style={{display: layerDisplay? 'block':'none'}}>
                <p>{props.base? props.base.HP :  ''}</p>
                <p>{props.base? props.base.Attack :  ''}</p>
                <p>{props.base? props.base.Defense :  ''}</p>
                <p>{props.base? props.base.Sp. Attack :  ''}</p>
                <p>{props.base? props.base.Sp. Defense :  ''}</p>
                <p>{props.base? props.base.Speed :  ''}</p>

            </div>
        </div>
    )
}
export default Pokemon;
