import { useState } from "react";
import "./App.css";
import pokemons from "./pokemons.json";
import Pokemon from "./Pokemon.jsx";

function App() {
  const [page, setPage] = useState(0);
  return (
    <div className="App">
      {pokemons.slice(page * 50, (page + 1) * 50).map((poke) => (
        <Pokemon
          key={poke.id}
          name={poke.name.english}
          type={poke.type}
          image={poke.image.thumbnail}
          base={poke.base}
        />
      ))}

      <button onClick={() => (page == 0 ? setPage(page) : setPage(page - 1))}>
        Previous
      </button>

      <button onClick={() => (page == 18 ? setPAge(page) : setPage(page + 1))}>Next</button>

    </div>
  );
}

export default App;
