import { useState } from "react";
import pokemons from "./pokemons.json";

function Pokemon(props) {
  const [layerDisplay, setLayerDisplay] = useState(false);
  return (
    <div>
      <p>{props.id}</p>
      <p>{props.name}</p>
      <p>
        {props.type.map((i) => (
          <li>{i}</li>
        ))}
      </p>
      <img src={props.image} />
      <button onClick={(e) => setLayerDisplay(!layerDisplay)}>Leer mas</button>

      <div style={{ display: layerDisplay ? "block" : "none" }}>
        <p>HP: {props.base ? props.base.HP : ""}</p>
        <p>Attack: {props.base ? props.base.Attack : ""}</p>
        <p>Defense: {props.base ? props.base.Defense : ""}</p>
        <p>spAttack: {props.base ? props.base.spAttack : ""}</p>
        <p>spDefense: {props.base ? props.base.spDefense : ""}</p>
        <p>Speed: {props.base ? props.base.Speed : ""}</p>
      </div>
      <hr />
    </div>
  );
}
export default Pokemon;
